const sakuraCanvas = document.getElementById('sakura-canvas');
let petals = [];
function SakuraPetal() {
  this.x = Math.random() * window.innerWidth;
  this.y = Math.random() * -60;
  this.w = 28 + Math.random() * 13;
  this.h = 14 + Math.random() * 10;
  this.amp = 40 + Math.random() * 32;
  this.phase = Math.random() * 6.28;
  this.speed = 1.1 + Math.random() * 0.85;
  this.sway = Math.random() * 0.7 + 0.4;
  this.rotate = Math.random() * 360;
  this.rotateSpeed = (Math.random()-0.5) * 1.8;
  this.opacity = 0.55 + Math.random() * 0.37;
  this.pink = `rgba(255,${190+Math.floor(Math.random()*30)},${223+Math.floor(Math.random()*20)},`;
}
SakuraPetal.prototype.draw = function(ctx, t) {
  let xx = this.x + Math.sin(t/750+this.phase) * this.amp * this.sway;
  ctx.save();
  ctx.globalAlpha = this.opacity;
  ctx.translate(xx, this.y);
  ctx.rotate(((this.rotate + t/5) % 360) * Math.PI / 180);
  ctx.beginPath();
  ctx.moveTo(0,0);
  ctx.bezierCurveTo(this.w/2,-this.h*.6, this.w,this.h*0.13, this.w*0.08,this.h*0.85);
  ctx.bezierCurveTo(-this.w*0.97,this.h*1.0, -this.w/2,0, 0,0);
  ctx.closePath();
  ctx.fillStyle = this.pink + "0.92)";
  ctx.strokeStyle = this.pink + "0.62)";
  ctx.lineWidth = 1.09;
  ctx.fill();
  ctx.stroke();
  ctx.restore();
};
function createPetals(number = 30) {
  petals = [];
  for(let i=0;i<number;i++) petals.push(new SakuraPetal());
}
function sakuraLoop() {
  const ctx = sakuraCanvas.getContext('2d');
  sakuraCanvas.width = window.innerWidth;
  sakuraCanvas.height = window.innerHeight;
  ctx.clearRect(0,0,sakuraCanvas.width,sakuraCanvas.height);
  let time = Date.now();
  for(let petal of petals){
    petal.y += petal.speed;
    petal.x += Math.sin(time/1200 + petal.phase) * 0.2;
    petal.rotate += petal.rotateSpeed;
    petal.draw(ctx, time);
    if(petal.y > window.innerHeight + 28){
      Object.assign(petal, new SakuraPetal());
      petal.x = Math.random() * window.innerWidth;
      petal.y = -Math.random()*90 - 15;
    }
  }
  requestAnimationFrame(sakuraLoop);
}
window.addEventListener('resize', ()=>{createPetals(Math.floor(window.innerWidth/36));});
createPetals(Math.floor(window.innerWidth/36));
sakuraLoop();

// ANIMATIONS/SCROLL
function onScrollShowBlocks() {
  let animatedElems = document.querySelectorAll('.animated-fade-in:not(.visible), .animated-fade-in-up:not(.visible)');
  let wh = window.innerHeight;
  animatedElems.forEach(el=>{
    let rect = el.getBoundingClientRect();
    if(rect.top < wh-80) el.classList.add('visible');
  });
}
window.addEventListener('scroll', onScrollShowBlocks);
window.addEventListener('DOMContentLoaded', onScrollShowBlocks);

