// Determine skin type by vote
function defineSkinType(answers) {
  // answers: [val1, val2, val3]
  const counts = {};
  answers.forEach(val=>{
    if(val&&(val==="acne-prone"||val==="dry"||val==="oily"||val==="normal"||val==="combination")){
      counts[val] = (counts[val]||0)+1;
      // if same votes, pick first
    }
  });
  // Find the max occurrence
  let skinType = Object.keys(counts).reduce((a,b)=>counts[a]>(counts[b]||0)?a:b, answers[0]);
  return skinType;
}

