document.getElementById('skin-test-form').addEventListener('submit', function(e){
  e.preventDefault();
  const a1 = this['after-wash'].value;
  const a2 = this['shine'].value;
  const a3 = this['imperfections'].value;
  if (!a1||!a2||!a3) return;
  const answers = [a1,a2,a3];
  const type = defineSkinType(answers);
  
  const res = `<span style="font-weight:bold;font-size:1.24em;">${skinTypeNames[type]}</span>
    <br>${skinTypeAdvice[type]}`;
  document.getElementById('skin-test-result').innerHTML = res;
  
  // Show product recommendations
  showRecProducts(type);
});

function showRecProducts(type){
  let rec = skinProducts[type]||[];
  let el = document.getElementById('rec-products');
  if(!el) return;
  if(rec.length === 0){
    el.innerHTML = "<p>Введите ответы на тест!</p>";
    return;
  }
  el.innerHTML = rec.map(p=>`
    <div class="rec-product-card animated-fade-in-up" tabindex="0" data-img="${p.img}" data-name="${p.name}" data-desc="${p.desc}" data-price="${p.price}" data-where="${p.where}">
      <img src="${p.img}" alt="${p.name}">
      <h3>${p.name}</h3>
      <div class="product-price">${p.price}</div>
      <div style="font-size:.97em">${p.desc}</div>
      <div style="font-size:.84em;color:#af38ad">${p.where}</div>
    </div>
  `).join('');
}

