// Modal logic
const modal = document.getElementById('product-modal');
const modalImg = document.getElementById('modal-img');
const modalInfo = document.getElementById('modal-info');
document.addEventListener('click', function(e){
  let card = e.target.closest('.product-card, .rec-product-card');
  if(card){
    let name = card.getAttribute('data-name');
    let img = card.getAttribute('data-img');
    let desc = card.getAttribute('data-desc');
    let price = card.getAttribute('data-price') || '';
    let type = card.getAttribute('data-type') || '';
    let where = card.getAttribute('data-where') || '';
    modalImg.innerHTML = `<img src="${img}" alt="${name}">`;
    modalInfo.innerHTML = `
      <h3 style="margin-top:0;">${name}</h3>
      ${type ? `<div style="color:#ee388c;font-size:1.04em;">Тип: ${type}</div>` : ""}
      <div style="padding:.4em 0;font-size:1.1em;">${desc}</div>
      <div class="product-price">${price}</div>
      ${where? `<div style="font-size:.95em;color:#ba37ca">Где купить: ${where}</div>`:""}
    `;
    modal.classList.add('active');
  }
  // Modal close logic
  if(e.target.classList.contains('modal-close') || e.target === modal){
    modal.classList.remove('active');
  }
});

// SPARKLE BUTTON EFFECT
function sparkleEffect(el) {
  if(!el) return;
  el.classList.add('sparking');
  setTimeout(()=>el.classList.remove('sparking'), 650);
}
document.addEventListener('click',function(e){
  let btn = e.target.closest('.fancy-btn');
  if(btn) sparkleEffect(btn);
});

